"use client";

// Example usage of ProductVariant_01 component

import { useState } from "react";
import ProductVariant_01, {
  VariantItem,
  VariantSelectionPayload,
} from "@/components/commerce-ui/product-variants-01";

const EXAMPLE_VARIANTS: VariantItem[] = [
  {
    availableQuantity: 5,
    id: "variant-sport",
    imageUrl:
      "https://raw.githubusercontent.com/stackzero-labs/ui/refs/heads/main/public/placeholders/headphone-1.jpg",
    isInStock: true,
    label: "Sport",
    price: 109.99,
    value: "variant-sport",
  },
  {
    availableQuantity: 10,
    id: "variant-prosound",
    imageUrl:
      "https://raw.githubusercontent.com/stackzero-labs/ui/refs/heads/main/public/placeholders/headphone-2.jpg",
    isInStock: true,
    label: "ProSound",
    price: 99.99,
    salePrice: 89.99,
    value: "variant-prosound",
  },
  {
    availableQuantity: 0,
    id: "variant-ultraquiet",
    imageUrl:
      "https://raw.githubusercontent.com/stackzero-labs/ui/refs/heads/main/public/placeholders/headphone-3.jpg",
    isInStock: false,
    label: "UltraQuiet™",
    price: 89.99,
    value: "variant-ultraquiet",
  },
  {
    availableQuantity: 2,
    id: "variant-extremesilence",
    imageUrl:
      "https://raw.githubusercontent.com/stackzero-labs/ui/refs/heads/main/public/placeholders/headphone-4.jpg",
    isInStock: true,
    label: "ExtremeSilence™",
    price: 119.99,
    salePrice: 99.99,
    value: "variant-extremesilence",
  },
];

export default function Product() {
  const [selectedVariant, setSelectedVariant] = useState<string>(
    EXAMPLE_VARIANTS[0].value
  );
  const [quantity, setQuantity] = useState<number>(1);

  // Adjust quantity if it exceeds available stock when variant changes
  const handleVariantChange = (value: string) => {
    setSelectedVariant(value);
    const newVariant = EXAMPLE_VARIANTS.find((v) => v.value === value);
    if (
      newVariant &&
      newVariant.isInStock &&
      newVariant.availableQuantity !== undefined &&
      newVariant.availableQuantity !== null &&
      quantity > newVariant.availableQuantity
    ) {
      setQuantity(Math.max(1, newVariant.availableQuantity));
    }
  };

  const handleAddToCart = (payload: VariantSelectionPayload) => {
    const variant = EXAMPLE_VARIANTS.find((v) => v.value === payload.variantId);
    const stockStatus = variant?.isInStock ? "In Stock" : "Out of Stock";
    window.alert(
      `Added ${payload.quantity} ${payload.variantLabel} to cart at $${payload.price} each.\nStock Status: ${stockStatus}`
    );
  };

  const handleBuyNow = (payload: VariantSelectionPayload) => {
    const variant = EXAMPLE_VARIANTS.find((v) => v.value === payload.variantId);
    const stockStatus = variant?.isInStock ? "In Stock" : "Out of Stock";
    window.alert(
      `Buying ${payload.quantity} ${payload.variantLabel} at $${payload.price} each.\nStock Status: ${stockStatus}`
    );
  };

  return (
    <div className="w-full">
      <ProductVariant_01
        title="Premium Noise-Cancelling Headphones"
        description="Experience exceptional sound quality with our top-of-the-line noise-cancelling headphones, perfect for music lovers and professionals alike."
        badge="Sale"
        variants={EXAMPLE_VARIANTS}
        variantLabel="Color"
        shippingInfo="Free 2-day shipping"
        selectedVariant={selectedVariant}
        onVariantChange={handleVariantChange}
        quantity={quantity}
        onQuantityChange={setQuantity}
        onAddToCart={handleAddToCart}
        onBuyNow={handleBuyNow}
      />
    </div>
  );
}

"use client";

import { Search, ShoppingCart, User, Menu } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";

interface StoreHeaderProps {
  storeName?: string;
  logoUrl?: string;
  searchPlaceholder?: string;
  className?: string;
  navigationItems?: { label: string; url: string }[];
  cartItemCount?: number;
  showUserAccount?: boolean;
  showMobileMenu?: boolean;
}

const DEFAULT_LOGO =
  "https://raw.githubusercontent.com/stackzero-labs/ui/refs/heads/main/public/placeholders/logo-01.png";

export function StoreHeader({
  cartItemCount = 0,
  className = "",
  logoUrl = DEFAULT_LOGO,
  navigationItems = [
    { label: "Home", url: "#" },
    { label: "Categories", url: "#" },
    { label: "Deals", url: "#" },
    { label: "About", url: "#" },
  ],
  searchPlaceholder = "Search products...",
  showMobileMenu = true,
  showUserAccount = true,
  storeName = "TechGadgets Store",
}: StoreHeaderProps) {
  return (
    <header
      className={`w-full border-b border-gray-200 shadow-sm dark:border-gray-800 ${className} bg-background`}
    >
      <div className="mx-auto w-full">
        <div className="flex h-16 items-center justify-between">
          {/* Store Logo and Name */}
          <div className="flex items-center space-x-3">
            <div className="h-10 w-10 overflow-hidden rounded-full bg-gray-100 p-1 dark:bg-gray-800">
              <img
                src={logoUrl}
                alt={`${storeName} logo`}
                width={40}
                height={40}
                className="h-full w-full object-contain"
              />
            </div>
            <h1 className="text-xl font-bold text-gray-900 dark:text-gray-100">
              {storeName}
            </h1>
          </div>

          {/* Navigation Menu - Hidden on mobile */}
          <nav className="hidden items-center space-x-8 md:flex">
            {navigationItems.map((item, index) => (
              <a
                key={index}
                href={item.url}
                className="text-sm text-gray-700 uppercase transition-colors hover:text-gray-900 dark:text-gray-300 dark:hover:text-gray-100"
              >
                {item.label}
              </a>
            ))}
          </nav>

          {/* Right Side: Search + Actions */}
          <div className="flex items-center space-x-4">
            {/* Search Input */}
            <div className="hidden w-64 sm:block">
              <div className="relative">
                <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                  <Search className="h-4 w-4 text-gray-400" />
                </div>
                <Input
                  type="text"
                  placeholder={searchPlaceholder}
                  className="w-full border-gray-300 pr-4 pl-10 focus:border-blue-500 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-800 dark:text-gray-100"
                />
              </div>
            </div>

            {/* Mobile Search Button */}
            <Button variant="ghost" size="sm" className="sm:hidden">
              <Search className="h-5 w-5" />
            </Button>

            {/* Cart */}
            <Button variant="ghost" size="sm" className="relative">
              <ShoppingCart className="h-5 w-5" />
              {cartItemCount > 0 && (
                <span className="absolute -top-1 -right-1 flex h-5 w-5 items-center justify-center rounded-full bg-red-500 text-xs text-white">
                  {cartItemCount > 9 ? "9+" : cartItemCount}
                </span>
              )}
            </Button>

            {/* User Account */}
            {showUserAccount && (
              <Button variant="ghost" size="sm">
                <User className="h-5 w-5" />
              </Button>
            )}

            {/* Mobile Menu */}
            {showMobileMenu && (
              <Button variant="ghost" size="sm" className="md:hidden">
                <Menu className="h-5 w-5" />
              </Button>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}

export default StoreHeader;
