"use client";

import * as React from "react";

import { cn } from "@/lib/utils";
import { ToggleGroup, ToggleGroupItem } from "@radix-ui/react-toggle-group";
interface VariantItem {
  id: string;
  value: string;
  label: string;
}

const variants: VariantItem[] = [
  { id: "variant-xxs", value: "variant-xxs", label: "XXS" },
  { id: "variant-xs", value: "variant-xs", label: "XS" },
  { id: "variant-s", value: "variant-s", label: "S" },
  { id: "variant-m", value: "variant-m", label: "M" },
  { id: "variant-l", value: "variant-l", label: "L" },
  { id: "variant-xl", value: "variant-xl", label: "XL" },
  { id: "variant-xxl", value: "variant-xxl", label: "XXL" },
];

const VariantSelector_04 = ({ className }: { className?: string }) => {
  const handleSelect = (value: string) => {
    console.log("Selected variant:", value);
  };
  return (
    <fieldset className={cn("space-y-4", className)}>
      <legend className="sr-only">Select a variant</legend>
      <ToggleGroup
        className="bg-mauve6 shadow-blackA4 inline-flex space-x-px rounded shadow-[0_2px_10px]"
        type="single"
        defaultValue="center"
        aria-label="Text alignment"
      >
        <ToggleGroupItem value="left" aria-label="Left aligned">
          One
        </ToggleGroupItem>
        <ToggleGroupItem value="center" aria-label="Center aligned">
          Two
        </ToggleGroupItem>
        <ToggleGroupItem value="right" aria-label="Right aligned">
          Three
        </ToggleGroupItem>
      </ToggleGroup>
    </fieldset>
  );
};

export default VariantSelector_04;
